<?php
if ( ! defined( 'ABSPATH' ) ) exit ; class SEUR_admin_class { public function __construct() { add_action( 'SEUR_init', array( $this, 'inicializo' ), 12 ) ; return $this ;  } function inicializo () { add_filter ( 'manage_shop_order_posts_columns'		, array( $this,	'SEUR_columns_head')					) ; add_action ( 'manage_shop_order_posts_custom_column', array( $this, 'SEUR_columns_content')			, 10, 3	) ;  } function SEUR_columns_head($columns) { $news = array() ; foreach($columns as $key => $col) { if($key == 'order_total' )	$news['SEUR'] = 'SEUR' ; $news[$key] = $col ;  } return $news ;  } function SEUR_columns_content($column_name, $post_id) { if ($column_name == 'SEUR') { echo SEUR()->API->get_estado_envio_str( $post_id ) ;  }  }  } 